mapper.select:
SELECT
-- 级联表的字段 ,kecheng.kecheng_name as kechengName ,kecheng.kecheng_photo as kechengPhoto ,kecheng.kecheng_video as kechengVideo ,kecheng.kecheng_moeny as kechengMoeny ,kecheng.kecheng_types as kechengTypes ,kecheng.jiaoshi_id as jiaoshiId ,kecheng.kecheng_content as kechengContent ,yonghu.yonghu_name as yonghuName ,yonghu.sex_types as sexTypes ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.new_money as newMoney
FROM kecheng_order a left JOIN kecheng kecheng ON a.kecheng_id = kecheng.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.kecheng_id = #{params.kechengId}
and a.yonghu_id = #{params.yonghuId}
and a.moeny >= #{params.moenyStart}
and a.moeny <= #{params.moenyEnd}
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') >= DATE_FORMAT(#{params.insertTimeStart}, '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{params.insertTimeEnd} , '%Y-%m-%d %H:%T:%s')
and kecheng.kecheng_name like CONCAT('%',#{params.kechengName},'%')
and kecheng.kecheng_video like CONCAT('%',#{params.kechengVideo},'%')
and kecheng.kecheng_moeny >= #{params.kechengMoenyStart}
and kecheng.kecheng_moeny <= #{params.kechengMoenyEnd}
and kecheng.kecheng_types = #{params.kechengTypes}
and kecheng.jiaoshi_id = #{params.jiaoshiId}
and kecheng.kecheng_content like CONCAT('%',#{params.kechengContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.sex_types = #{params.sexTypes}
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
order by a.${params.orderBy} desc
